import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import QrScanner from 'react-qr-scanner';

function App() {
  const [result, setResult] = useState(null);

  const handleScan = (data) => {
    if (data) {
      setResult(data);
    }
  }

  return (
    <div>
      <h1>Welcome to Our App</h1>
      <Link to="/scan">
        <button>Scan QR</button>
      </Link>
      <hr />
      <h2>About Us</h2>
      <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed et sollicitudin erat. Integer eget nulla nibh. Nam velit velit, mollis vel varius vel, posuere vel velit. Sed eget enim quis velit tempus aliquam.</p>
      <h2>Contact Us</h2>
      <p>Email: info@ourapp.com</p>
      <p>Phone: 555-555-5555</p>
      {result && <p>Scanned QR Code: {result}</p>}
    </div>
  );
}

export default App;
